import React, { useEffect, useState } from "react";
import { obtenerVinos, eliminarVino } from "../services/vinoService";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { InputText } from "primereact/inputtext";
import { Button } from "primereact/button";
import { Paginator } from "primereact/paginator";

const VinosPage = () => {
  const [vinos, setVinos] = useState([]);
  const [totalRecords, setTotalRecords] = useState(0);
  const [loading, setLoading] = useState(true);
  const [nombreFiltro, setNombreFiltro] = useState("");
  const [page, setPage] = useState(1);
  const [rows, setRows] = useState(10);
  const [sortField, setSortField] = useState("nombre");
  const [sortOrder, setSortOrder] = useState(1);

  // Función para obtener los vinos con filtros, paginación y ordenamiento
  const fetchVinos = async () => {
    setLoading(true);
    const params = {
      nombre: nombreFiltro,
      page,
      limit: rows,
      sortBy: sortField,
      order: sortOrder === 1 ? "asc" : "desc",
    };
    try {
      const data = await obtenerVinos(params);
      setVinos(data.vinos); // El backend debe devolver los vinos y el total
      setTotalRecords(data.total); // Total de registros para la paginación
    } catch (error) {
      console.error("Error al obtener los vinos:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchVinos();
  }, [nombreFiltro, page, rows, sortField, sortOrder]);

  const onPageChange = (event) => {
    setPage(event.page + 1); // PrimeReact usa index 0 para la primera página
    setRows(event.rows);
  };

  const onSort = (event) => {
    setSortField(event.sortField);
    setSortOrder(event.sortOrder);
  };

  const handleEliminar = async (id) => {
    try {
      await eliminarVino(id);
      fetchVinos(); // Refrescar la lista después de eliminar
    } catch (error) {
      console.error("Error al eliminar el vino:", error);
    }
  };

  const actionBodyTemplate = (rowData) => {
    return (
      <Button
        label="Eliminar"
        icon="pi pi-trash"
        className="p-button-danger"
        onClick={() => handleEliminar(rowData._id)}
      />
    );
  };

  return (
    <div className="p-grid p-justify-center">
      <div className="p-col-12 p-md-8">
        <h1>Vinos</h1>

        {/* Filtro por nombre */}
        <span className="p-input-icon-left" style={{ marginBottom: '1rem' }}>
          <i className="pi pi-search" />
          <InputText
            value={nombreFiltro}
            onChange={(e) => setNombreFiltro(e.target.value)}
            placeholder="Buscar por nombre"
          />
        </span>

        {/* Tabla de vinos con paginación y ordenamiento */}
        <DataTable
          value={vinos}
          loading={loading}
          paginator={true}
          rows={rows}
          totalRecords={totalRecords}
          lazy={true}
          first={(page - 1) * rows}
          onPage={onPageChange}
          onSort={onSort}
          sortField={sortField}
          sortOrder={sortOrder}
          responsiveLayout="scroll"
        >
          <Column field="nombre" header="Nombre" sortable />
          <Column field="tipo" header="Tipo" sortable />
          <Column
            body={actionBodyTemplate}
            header="Acciones"
          />
        </DataTable>
      </div>
    </div>
  );
};

export default VinosPage;
