import React, { useState, useEffect } from "react";
import { obtenerTodosLosVinos, eliminarVino, obtenerVinoPorId } from "../services/vinoService";
import VinoList from "./VinoList";
import VinoDetail from "./VinoDetail";

const VinosPage = () => {
  const [vinos, setVinos] = useState([]);
  const [selectedVino, setSelectedVino] = useState(null);

  // Cargar todos los vinos al montar el componente
  useEffect(() => {
    const cargarVinos = async () => {
      try {
        const data = await obtenerTodosLosVinos();
        setVinos(data);
      } catch (error) {
        console.error("Error al obtener los vinos", error);
      }
    };

    cargarVinos();
  }, []);

  // Manejar la eliminación de un vino
  const handleEliminarVino = async (id) => {
    try {
      await eliminarVino(id);
      setVinos((prevVinos) => prevVinos.filter((vino) => vino._id !== id)); // Actualizar la lista de vinos
      alert("Vino eliminado correctamente");
    } catch (error) {
      console.error("Error al eliminar el vino", error);
      alert("Hubo un error al eliminar el vino");
    }
  };

  // Manejar la selección de un vino
  const handleSelectVino = async (id) => {
    try {
      const vino = await obtenerVinoPorId(id);
      setSelectedVino(vino); // Guardar el vino seleccionado en el estado
    } catch (error) {
      console.error("Error al obtener los detalles del vino", error);
      alert("Error al cargar los detalles del vino");
    }
  };

  return (
    <div>
      <h1>Gestión de Vinos</h1>

      {/* Lista de vinos */}
      <VinoList vinos={vinos} onDeleteVino={handleEliminarVino} onSelectVino={handleSelectVino} />

      {/* Detalles del vino */}
      <VinoDetail vino={selectedVino} />
    </div>
  );
};

export default VinosPage;
