import React, { useState, useEffect } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
import { Paginator } from 'primereact/paginator';
import { Dialog } from 'primereact/dialog';
import { InputText } from 'primereact/inputtext';
import {
  obtenerBodegasConPaginado,
  crearBodega,
  actualizarBodega,
  eliminarBodega,
  agregarVinoABodega,
  eliminarVinoDeBodega,
} from '../services/BodegaService';

const BodegasPage = ({
    bodegas, setDialogVisible, setEditing, setBodega, handleDeleteBodega, page, totalRecords, onPageChange
  }) => {
    return (
      <div className="p-mt-5 p-p-4" style={{ backgroundColor: '#f4f4f9', borderRadius: '10px' }}>
        <h1 className="p-text-center p-mb-4">Gesti√≥n de Bodegas</h1>
        <div className="p-d-flex p-jc-between p-mb-4">
          <Button
            label="Nueva Bodega"
            icon="pi pi-plus"
            className="p-button-rounded p-button-success"
            onClick={() => {
              setDialogVisible(true);
              setEditing(false);
              setBodega({ nombre: '', vinos: [] });
            }}
          />
        </div>
  
        <DataTable
          value={bodegas}
          paginator={false}
          responsiveLayout="scroll"
          className="p-datatable-striped p-datatable-sm"
          style={{ minHeight: '300px' }}
        >
          <Column field="nombre" header="Nombre" />
          <Column field="vinos" header="Vinos" body={(rowData) => rowData.vinos.length} />
          <Column
            header="Acciones"
            body={(rowData) => (
              <div className="p-d-flex p-ai-center">
                <Button
                  icon="pi pi-pencil"
                  className="p-button-rounded p-button-warning p-mr-2"
                  onClick={() => {
                    setBodega(rowData);
                    setDialogVisible(true);
                    setEditing(true);
                  }}
                />
                <Button
                  icon="pi pi-trash"
                  className="p-button-rounded p-button-danger"
                  onClick={() => handleDeleteBodega(rowData._id)}
                />
              </div>
            )}
          />
        </DataTable>
  
        <Paginator
          first={(page - 1) * 10}
          rows={10}
          totalRecords={totalRecords}
          onPageChange={onPageChange}
          className="p-mt-4"
        />
      </div>
    );
  };

export default BodegasPage;
