import React, { useState, useEffect } from "react";
import {
  crearVino,
  obtenerVinosConPaginado,
  obtenerVinoPorId,
  obtenerVinosPorNombre,
  actualizarVino,
  eliminarVino,
} from "../services/vinoService";
import { InputText } from "primereact/inputtext";
import { Button } from "primereact/button";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Paginator } from "primereact/paginator";

const VinosPage = () => {
  // Estados para las funcionalidades
  const [vinos, setVinos] = useState([]);
  const [totalRecords, setTotalRecords] = useState(0);
  const [page, setPage] = useState(1);
  const [rows, setRows] = useState(10);
  const [loading, setLoading] = useState(true);
  const [nombreFiltro, setNombreFiltro] = useState("");

  // Obtener vinos con paginación y filtros
  const fetchVinosConPaginado = async () => {
    setLoading(true);
    const params = {
      page,
      limit: rows,
      nombre: nombreFiltro,
    };
    try {
      const data = await obtenerVinosConPaginado(params);
      setVinos(data.vinos || []);
      setTotalRecords(data.total || 0);
    } catch (error) {
      console.error("Error al obtener vinos con paginación:", error);
    } finally {
      setLoading(false);
    }
  };

  // Eliminar un vino por ID
  const handleEliminarVino = async (id) => {
    try {
      await eliminarVino(id);
      fetchVinosConPaginado(); // Refrescar la lista después de eliminar
    } catch (error) {
      console.error("Error al eliminar el vino:", error);
    }
  };

  useEffect(() => {
    fetchVinosConPaginado();
  }, [page, rows, nombreFiltro]);

  const onPageChange = (event) => {
    setPage(event.page + 1);
    setRows(event.rows);
  };

  const actionBodyTemplate = (rowData) => {
    return (
      <Button
        label="Eliminar"
        icon="pi pi-trash"
        className="p-button-danger"
        onClick={() => handleEliminarVino(rowData._id)}
      />
    );
  };

  return (
    <div className="p-grid p-justify-center">
      <div className="p-col-12 p-md-8">
        <h1>Gestión de Vinos</h1>

        {/* Filtro por nombre */}
        <span className="p-input-icon-left" style={{ marginBottom: '1rem' }}>
          <i className="pi pi-search" />
          <InputText
            value={nombreFiltro}
            onChange={(e) => setNombreFiltro(e.target.value)}
            placeholder="Buscar por nombre"
          />
        </span>

        {/* Tabla de vinos con paginación */}
        <DataTable value={vinos} loading={loading} responsiveLayout="scroll">
          <Column field="nombre" header="Nombre" sortable />
          <Column field="tipo" header="Tipo" sortable />
          <Column body={actionBodyTemplate} header="Acciones" />
        </DataTable>

        {/* Paginador */}
        <Paginator
          first={(page - 1) * rows}
          rows={rows}
          totalRecords={totalRecords}
          onPageChange={onPageChange}
        />
      </div>
    </div>
  );
};

export default VinosPage;
