import React, { useState, useEffect } from "react";
import { obtenerVinoPorId, actualizarVino } from "../services/vinoService";

const EditarVinoForm = ({ vinoId, onVinoActualizado, onCancel }) => {
  const [nombre, setNombre] = useState("");
  const [tipo, setTipo] = useState("");

  // Cargar los datos actuales del vino cuando el componente se monta
  useEffect(() => {
    const cargarDatosVino = async () => {
      try {
        const vino = await obtenerVinoPorId(vinoId);
        setNombre(vino.nombre);
        setTipo(vino.tipo);
      } catch (error) {
        console.error("Error al cargar los datos del vino", error);
        alert("Hubo un error al cargar los detalles del vino.");
      }
    };

    cargarDatosVino();
  }, [vinoId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const vinoActualizado = { nombre, tipo };
      const updatedVino = await actualizarVino(vinoId, vinoActualizado);
      onVinoActualizado(updatedVino); // Notificar que el vino ha sido actualizado
    } catch (error) {
      console.error("Error al actualizar el vino", error);
      alert("Hubo un error al actualizar el vino.");
    }
  };

  return (
    <div>
      <h2>Editar Vino</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre:</label>
          <input
            type="text"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Tipo:</label>
          <input
            type="text"
            value={tipo}
            onChange={(e) => setTipo(e.target.value)}
            required
          />
        </div>
        <button type="submit">Guardar cambios</button>
        <button type="button" onClick={onCancel}>Cancelar</button>
      </form>
    </div>
  );
};

export default EditarVinoForm;
