import React, { useState, useEffect } from 'react';
import { obtenerBodegas, obtenerBodegaPorId, crearBodega, actualizarBodega, eliminarBodega, agregarVinoABodega, eliminarVinoDeBodega, obtenerBodegasConPaginado } from '../services/BodegaService';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
import { Dialog } from 'primereact/dialog';
import { InputText } from 'primereact/inputtext';
import { Paginator } from 'primereact/paginator';

const BodegasPage = () => {
    const [bodegas, setBodegas] = useState([]);
    const [bodega, setBodega] = useState({ nombre: '', descripcion: '' });
    const [dialogVisible, setDialogVisible] = useState(false);
    const [editing, setEditing] = useState(false);
    const [page, setPage] = useState(1);
    const [totalRecords, setTotalRecords] = useState(0);

    useEffect(() => {
        fetchBodegasConPaginado(page);
    }, [page]);

    const fetchBodegas = async () => {
        try {
            const response = await obtenerBodegas();
            setBodegas(response.data);
        } catch (error) {
            console.error("Error al obtener bodegas", error);
        }
    };

    const fetchBodegasConPaginado = async (page) => {
        try {
            const response = await obtenerBodegasConPaginado(page, 10);
            setBodegas(response.data.bodegas);
            setTotalRecords(response.data.totalBodegas);
        } catch (error) {
            console.error("Error al obtener bodegas paginadas", error);
        }
    };

    const fetchBodegaPorId = async (id) => {
        try {
            const response = await obtenerBodegaPorId(id);
            setBodega(response.data);
        } catch (error) {
            console.error("Error al obtener bodega por ID", error);
        }
    };

    const handleCreateBodega = async () => {
        try {
            await crearBodega(bodega);
            setDialogVisible(false);
            fetchBodegasConPaginado(page);
        } catch (error) {
            console.error("Error al crear la bodega", error);
        }
    };

    const handleUpdateBodega = async () => {
        try {
            await actualizarBodega(bodega._id, bodega);
            setDialogVisible(false);
            fetchBodegasConPaginado(page);
        } catch (error) {
            console.error("Error al actualizar la bodega", error);
        }
    };

    const handleDeleteBodega = async (id) => {
        try {
            await eliminarBodega(id);
            fetchBodegasConPaginado(page);
        } catch (error) {
            console.error("Error al eliminar la bodega", error);
        }
    };

    const handleAddVino = async (id, vinoId) => {
        try {
            await agregarVinoABodega(id, vinoId);
            fetchBodegasConPaginado(page);
        } catch (error) {
            console.error("Error al agregar vino a la bodega", error);
        }
    };

    const handleRemoveVino = async (id, vinoId) => {
        try {
            await eliminarVinoDeBodega(id, vinoId);
            fetchBodegasConPaginado(page);
        } catch (error) {
            console.error("Error al eliminar vino de la bodega", error);
        }
    };

    const onPageChange = (event) => {
        setPage(event.page + 1);
    };

    return (
        <div>
            <h1>Gestión de Bodegas</h1>
            <Button label="Nueva Bodega" icon="pi pi-plus" onClick={() => setDialogVisible(true)} />
            <DataTable value={bodegas} paginator responsiveLayout="scroll">
                <Column field="nombre" header="Nombre" />
                <Column field="descripcion" header="Descripción" />
                <Column header="Acciones" body={(rowData) => (
                    <div>
                        <Button icon="pi pi-pencil" className="p-button-rounded p-button-warning" onClick={() => { setBodega(rowData); setDialogVisible(true); setEditing(true); }} />
                        <Button icon="pi pi-trash" className="p-button-rounded p-button-danger" onClick={() => handleDeleteBodega(rowData._id)} />
                    </div>
                )} />
            </DataTable>
            <Paginator first={(page - 1) * 10} rows={10} totalRecords={totalRecords} onPageChange={onPageChange} />

            <Dialog header={editing ? "Editar Bodega" : "Nueva Bodega"} visible={dialogVisible} style={{ width: '50vw' }} onHide={() => setDialogVisible(false)}>
                <div className="p-field">
                    <label htmlFor="nombre">Nombre</label>
                    <InputText id="nombre" value={bodega.nombre} onChange={(e) => setBodega({ ...bodega, nombre: e.target.value })} />
                </div>
                <div className="p-field">
                    <label htmlFor="descripcion">Descripción</label>
                    <InputText id="descripcion" value={bodega.descripcion} onChange={(e) => setBodega({ ...bodega, descripcion: e.target.value })} />
                </div>
                <Button label={editing ? "Actualizar" : "Crear"} icon="pi pi-check" onClick={editing ? handleUpdateBodega : handleCreateBodega} />
            </Dialog>
        </div>
    );
};

export default BodegasPage;
