import React, { useState, useEffect } from 'react';
import { 
  getAllVinos, 
  createVino, 
  updateVino, 
  deleteVino, 
  getVinoById 
} from '../services/vinoService';

const VinosCRUD = () => {
  const [vinos, setVinos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newVino, setNewVino] = useState({ nombre: '', tipo: '', bodega: '' });
  const [updateVinoId, setUpdateVinoId] = useState('');
  const [updateVinoData, setUpdateVinoData] = useState({ nombre: '', tipo: '', bodega: '' });
  const [deleteVinoId, setDeleteVinoId] = useState('');
  const [vinoId, setVinoId] = useState('');
  const [vinoDetail, setVinoDetail] = useState(null);

  // Obtener todos los vinos
  useEffect(() => {
    const fetchVinos = async () => {
      try {
        const data = await getAllVinos();
        setVinos(data);
      } catch (err) {
        setError('Error al cargar los vinos');
      } finally {
        setLoading(false);
      }
    };

    fetchVinos();
  }, []);

  // Crear un nuevo vino
  const handleCreateVino = async () => {
    try {
      const vinoCreado = await createVino(newVino);
      setVinos([...vinos, vinoCreado]);
      setNewVino({ nombre: '', tipo: '', bodega: '' });
    } catch (error) {
      setError('Error al crear el vino');
    }
  };

  // Actualizar un vino existente
  const handleUpdateVino = async () => {
    try {
      const vinoActualizado = await updateVino(updateVinoId, updateVinoData);
      setVinos(vinos.map(vino => (vino._id === updateVinoId ? vinoActualizado : vino)));
      setUpdateVinoData({ nombre: '', tipo: '', bodega: '' });
      setUpdateVinoId('');
    } catch (error) {
      setError('Error al actualizar el vino');
    }
  };

  // Eliminar un vino
  const handleDeleteVino = async () => {
    try {
      await deleteVino(deleteVinoId);
      setVinos(vinos.filter(vino => vino._id !== deleteVinoId));
      setDeleteVinoId('');
    } catch (error) {
      setError('Error al eliminar el vino');
    }
  };

  // Obtener un vino por ID
  const handleGetVinoById = async () => {
    try {
      const vino = await getVinoById(vinoId);
      setVinoDetail(vino);
    } catch (error) {
      setError('Error al obtener el vino por ID');
    }
  };

  if (loading) return <p>Cargando vinos...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h1>CRUD de Vinos</h1>

      {/* Lista de vinos */}
      <h2>Todos los Vinos</h2>
      <ul>
        {vinos.map(vino => (
          <li key={vino._id}>
            {vino.nombre} - {vino.tipo} - Bodega: {vino.bodega}
          </li>
        ))}
      </ul>

      {/* Crear un nuevo vino */}
      <h2>Crear un Vino</h2>
      <input 
        type="text" 
        placeholder="Nombre" 
        value={newVino.nombre} 
        onChange={(e) => setNewVino({ ...newVino, nombre: e.target.value })} 
      />
      <input 
        type="text" 
        placeholder="Tipo" 
        value={newVino.tipo} 
        onChange={(e) => setNewVino({ ...newVino, tipo: e.target.value })} 
      />
      <input 
        type="text" 
        placeholder="Bodega" 
        value={newVino.bodega} 
        onChange={(e) => setNewVino({ ...newVino, bodega: e.target.value })} 
      />
      <button onClick={handleCreateVino}>Crear Vino</button>

      {/* Actualizar un vino */}
      <h2>Actualizar un Vino</h2>
      <input 
        type="text" 
        placeholder="ID del vino" 
        value={updateVinoId} 
        onChange={(e) => setUpdateVinoId(e.target.value)} 
      />
      <input 
        type="text" 
        placeholder="Nuevo nombre" 
        value={updateVinoData.nombre} 
        onChange={(e) => setUpdateVinoData({ ...updateVinoData, nombre: e.target.value })} 
      />
      <input 
        type="text" 
        placeholder="Nuevo tipo" 
        value={updateVinoData.tipo} 
        onChange={(e) => setUpdateVinoData({ ...updateVinoData, tipo: e.target.value })} 
      />
      <input 
        type="text" 
        placeholder="Nueva bodega" 
        value={updateVinoData.bodega} 
        onChange={(e) => setUpdateVinoData({ ...updateVinoData, bodega: e.target.value })} 
      />
      <button onClick={handleUpdateVino}>Actualizar Vino</button>

      {/* Eliminar un vino */}
      <h2>Eliminar un Vino</h2>
      <input 
        type="text" 
        placeholder="ID del vino a eliminar" 
        value={deleteVinoId} 
        onChange={(e) => setDeleteVinoId(e.target.value)} 
      />
      <button onClick={handleDeleteVino}>Eliminar Vino</button>

      {/* Obtener un vino por ID */}
      <h2>Obtener Vino por ID</h2>
      <input 
        type="text" 
        placeholder="ID del vino" 
        value={vinoId} 
        onChange={(e) => setVinoId(e.target.value)} 
      />
      <button onClick={handleGetVinoById}>Buscar Vino por ID</button>

      {vinoDetail && (
        <div>
          <h3>Detalle del Vino:</h3>
          <p>Nombre: {vinoDetail.nombre}</p>
          <p>Tipo: {vinoDetail.tipo}</p>
          <p>Bodega: {vinoDetail.bodega}</p>
        </div>
      )}
    </div>
  );
};

export default VinosCRUD;
