// VinosPage.js
import React, { useEffect, useState } from 'react';
import { getAllVinos } from '../services/vinoServices';
import VinosList from '../components/VinosList';

const VinosPage = () => {
  const [vinos, setVinos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Llamada al servicio para obtener los vinos al montar el componente
  useEffect(() => {
    const fetchVinos = async () => {
      try {
        const data = await getAllVinos();
        setVinos(data);
      } catch (err) {
        setError('Error al cargar los vinos');
      } finally {
        setLoading(false);
      }
    };

    fetchVinos();
  }, []);

  if (loading) return <p>Cargando vinos...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h1>Vinos Disponibles</h1>
      <VinosList vinos={vinos} />
    </div>
  );
};

export default VinosPage;
