import React, { useState } from 'react';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { obtenerBodegaPorNombre } from '../../services/BodegaService'; // Importa el servicio

const BuscarBodegaPorNombre = () => {
  const [descripcion, setDescripcion] = useState('');
  const [bodega, setBodega] = useState(null);

  const handleBuscar = async () => {
    try {
      const response = await obtenerBodegaPorNombre(descripcion);
      setBodega(response.data);
    } catch (error) {
      console.error('Error al buscar la bodega', error);
    }
  };

  return (
    <div className="p-mt-4 p-p-4">
      <h3>Buscar Bodega por Nombre</h3>
      <div className="p-field">
        <InputText 
          value={descripcion} 
          onChange={(e) => setDescripcion(e.target.value)} 
          placeholder="Nombre de la bodega"
        />
        <Button label="Buscar" icon="pi pi-search" onClick={handleBuscar} className="p-ml-2" />
      </div>

      {bodega && (
        <div className="p-mt-4">
          <h4>Bodega Encontrada:</h4>
          <p><strong>Nombre:</strong> {bodega.nombre}</p>
          <p><strong>Vinos:</strong> {bodega.vinos.length}</p>
        </div>
      )}
    </div>
  );
};

export default BuscarBodegaPorNombre;
