import React, { useState, useEffect } from "react";
import { obtenerVinos, eliminarVino, obtenerVinoPorId } from "../services/vinoService";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { InputText } from "primereact/inputtext";
import { Button } from "primereact/button";
import { Paginator } from "primereact/paginator";

const ListadoVinosPage = () => {
  // Estados
  const [vinos, setVinos] = useState([]);
  const [totalRecords, setTotalRecords] = useState(0);
  const [loading, setLoading] = useState(true);
  const [nombreFiltro, setNombreFiltro] = useState("");
  const [page, setPage] = useState(1);
  const [rows, setRows] = useState(10);
  const [vinoPorId, setVinoPorId] = useState(null);
  const [vinoId, setVinoId] = useState("");

  // Función para obtener los vinos con filtros, paginación y ordenamiento
  const fetchVinos = async () => {
    setLoading(true);
    const params = {
      nombre: nombreFiltro,
      page,
      limit: rows,
    };
    try {
      const data = await obtenerVinos(params);
      setVinos(data.vinos || []);
      setTotalRecords(data.total || 0);
    } catch (error) {
      console.error("Error al obtener los vinos:", error);
    } finally {
      setLoading(false);
    }
  };

  // Función para obtener un vino por ID
  const handleObtenerVinoPorId = async () => {
    try {
      const data = await obtenerVinoPorId(vinoId);
      setVinoPorId(data);
    } catch (error) {
      console.error("Error al obtener el vino por ID:", error);
    }
  };

  // Función para eliminar un vino
  const handleEliminarVino = async (id) => {
    try {
      await eliminarVino(id);
      fetchVinos(); // Refrescar la lista después de eliminar
    } catch (error) {
      console.error("Error al eliminar el vino:", error);
    }
  };

  useEffect(() => {
    fetchVinos();
  }, [nombreFiltro, page, rows]);

  const onPageChange = (event) => {
    setPage(event.page + 1);
    setRows(event.rows);
  };

  const actionBodyTemplate = (rowData) => {
    return (
      <Button
        label="Eliminar"
        icon="pi pi-trash"
        className="p-button-danger"
        onClick={() => handleEliminarVino(rowData._id)}
      />
    );
  };

  return (
    <div className="p-grid p-justify-center">
      <div className="p-col-12 p-md-8">
        <h1>Listado y Gestión de Vinos</h1>

        {/* Filtro por nombre */}
        <span className="p-input-icon-left" style={{ marginBottom: '1rem' }}>
          <i className="pi pi-search" />
          <InputText
            value={nombreFiltro}
            onChange={(e) => setNombreFiltro(e.target.value)}
            placeholder="Buscar por nombre"
          />
        </span>

        {/* Tabla de vinos con paginación y ordenamiento */}
        <DataTable
          value={vinos}
          loading={loading}
          paginator={false}
          rows={rows}
          totalRecords={totalRecords}
          lazy={true}
          first={(page - 1) * rows}
          responsiveLayout="scroll"
        >
          <Column field="nombre" header="Nombre" sortable />
          <Column field="tipo" header="Tipo" sortable />
          <Column body={actionBodyTemplate} header="Acciones" />
        </DataTable>

        {/* Paginador */}
        <Paginator
          first={(page - 1) * rows}
          rows={rows}
          totalRecords={totalRecords}
          onPageChange={onPageChange}
        />

        {/* Obtener vino por ID */}
        <h3>Obtener Vino por ID</h3>
        <InputText
          placeholder="ID del vino"
          value={vinoId}
          onChange={(e) => setVinoId(e.target.value)}
        />
        <Button label="Obtener Vino" onClick={handleObtenerVinoPorId} />
        {vinoPorId && (
          <div style={{ marginTop: '1rem' }}>
            <p><strong>Nombre:</strong> {vinoPorId.nombre}</p>
            <p><strong>Tipo:</strong> {vinoPorId.tipo}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ListadoVinosPage;
