import React from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Button } from "primereact/button";

export const VinoList = ({ vinos, onSelectVino, onDeleteVino }) => {
  // Verificación si el array de vinos está vacío o no se ha cargado
  if (!vinos || vinos.length === 0) {
    return <p>No hay vinos disponibles.</p>;
  }

  // Plantilla de acciones para cada fila (ver detalle y eliminar)
  const actionBodyTemplate = (rowData) => {
    console.log('Eliminando vino con ID:', rowData._id); // Depuración
    return (
      <div>
        <Button
          icon="pi pi-trash"
          className="p-button-rounded p-button-danger"
          onClick={() => onDeleteVino(rowData._id)}
        />
      </div>
    );
  };
  

  return (
    <DataTable
      value={vinos}
      responsiveLayout="scroll"
      dataKey="_id" // Clave única para cada vino
    >
      <Column field="nombre" header="Nombre" />
      <Column field="tipo" header="Tipo" />
      <Column body={actionBodyTemplate} header="Acciones" />
    </DataTable>
  );
};
