import React, { useState } from "react";
import { agregarVino } from "../services/vinoService";

const AgregarVinoForm = ({ onVinoAgregado }) => {
  const [nombre, setNombre] = useState("");
  const [tipo, setTipo] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const nuevoVino = { nombre, tipo };
      const vinoAgregado = await agregarVino(nuevoVino);
      onVinoAgregado(vinoAgregado); // Notificar que un vino ha sido agregado
      setNombre("");
      setTipo("");
    } catch (error) {
      alert("Error al agregar el vino");
    }
  };

  return (
    <div>
      <h2>Agregar Nuevo Vino</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre:</label>
          <input
            type="text"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Tipo:</label>
          <input
            type="text"
            value={tipo}
            onChange={(e) => setTipo(e.target.value)}
            required
          />
        </div>
        <button type="submit">Agregar Vino</button>
      </form>
    </div>
  );
};

export default AgregarVinoForm;
