import React, { useState, useEffect, useRef } from "react";
import { obtenerTodosLosVinos, eliminarVino } from "../services/vinoService";
import { VinoList } from "../components/VinoList";
import { VinoDetail } from "../components/VinoDetail";
import { Toast } from "primereact/toast";
import { Dialog } from "primereact/dialog";
import { confirmDialog } from "primereact/confirmdialog";

const VinosPage = () => {
  const [vinos, setVinos] = useState([]);
  const [selectedVino, setSelectedVino] = useState(null);
  const [dialogVisible, setDialogVisible] = useState(false);
  const toast = useRef(null);

  useEffect(() => {
    const cargarVinos = async () => {
      try {
        const data = await obtenerTodosLosVinos();
        setVinos(data); // Asegúrate de que estás guardando todos los vinos en el estado.
      } catch (error) {
        console.error("Error al obtener los vinos", error);
      }
    };
    cargarVinos(); // Llama la función que carga los vinos
  }, []); // Usa un array vacío para que se ejecute solo una vez
  

  const confirmarEliminarVino = (id) => {
    confirmDialog({
      message: "¿Está seguro de que desea eliminar este vino?",
      header: "Confirmación",
      icon: "pi pi-exclamation-triangle",
      accept: () => handleEliminarVino(id),
    });
  };

  const handleEliminarVino = async (id) => {
    try {
      await eliminarVino(id);
      toast.current.show({
        severity: "success",
        summary: "Éxito",
        detail: "Vino eliminado",
        life: 3000,
      });
      cargarVinos();
    } catch (error) {
      toast.current.show({
        severity: "error",
        summary: "Error",
        detail: "Error al eliminar el vino",
        life: 3000,
      });
    }
  };

  const handleSelectVino = (vino) => {
    setSelectedVino(vino);
    setDialogVisible(true);
  };

  return (
    <div>
      <Toast ref={toast} />
      <h1>Lista de Vinos</h1>
      <VinoList vinos={vinos} onSelectVino={handleSelectVino} onDeleteVino={confirmarEliminarVino} />
      <Dialog header="Detalle del Vino" visible={dialogVisible} onHide={() => setDialogVisible(false)} style={{ width: "50vw" }}>
        {selectedVino && <VinoDetail vino={selectedVino} />}
      </Dialog>
    </div>
  );
};

export default VinosPage;
