import React, { useState, useEffect } from 'react';
import { 
  getAllVinos, 
  createVino, 
  updateVino, 
  deleteVino, 
  getVinoById, 
  getVinoByNombre 
} from '../services/vinoService';

const VinosCRUD = () => {
  const [vinos, setVinos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newVino, setNewVino] = useState({ nombre: '', tipo: '', bodega: '' });
  const [vinoId, setVinoId] = useState('');
  const [vinoById, setVinoById] = useState(null);
  const [nombreBusqueda, setNombreBusqueda] = useState('');
  const [vinosPorNombre, setVinosPorNombre] = useState([]);
  
  // Obtener todos los vinos al cargar el componente
  useEffect(() => {
    const fetchVinos = async () => {
      try {
        const data = await getAllVinos();
        setVinos(data);
      } catch (err) {
        setError('Error al cargar los vinos');
      } finally {
        setLoading(false);
      }
    };

    fetchVinos();
  }, []);

  // Crear un nuevo vino
  const handleCreateVino = async () => {
    try {
      const vinoCreado = await createVino(newVino);
      setVinos([...vinos, vinoCreado]);
      setNewVino({ nombre: '', tipo: '', bodega: '' }); // Limpiar formulario
    } catch (error) {
      setError('Error al crear el vino');
    }
  };

  // Eliminar un vino
  const handleDeleteVino = async (id) => {
    try {
      await deleteVino(id);
      setVinos(vinos.filter(vino => vino._id !== id));
    } catch (error) {
      setError('Error al eliminar el vino');
    }
  };

  // Buscar vino por ID
  const handleGetVinoById = async () => {
    try {
      const vino = await getVinoById(vinoId);
      setVinoById(vino);
    } catch (error) {
      setError('Error al obtener vino por ID');
    }
  };

  // Buscar vinos por nombre
  const handleGetVinoByNombre = async () => {
    try {
      const vinos = await getVinoByNombre(nombreBusqueda);
      setVinosPorNombre(vinos);
    } catch (error) {
      setError('Error al buscar vinos por nombre');
    }
  };

  if (loading) return <p>Cargando vinos...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h1>CRUD de Vinos</h1>

      {/* Lista de vinos */}
      <h2>Todos los Vinos</h2>
      <ul>
        {vinos.map(vino => (
          <li key={vino._id}>
            {vino.nombre} - {vino.tipo} - Bodega: {vino.bodega}
            <button onClick={() => handleDeleteVino(vino._id)}>Eliminar</button>
          </li>
        ))}
      </ul>

      {/* Crear un nuevo vino */}
      <h2>Crear un Vino</h2>
      <input 
        type="text" 
        placeholder="Nombre" 
        value={newVino.nombre} 
        onChange={(e) => setNewVino({ ...newVino, nombre: e.target.value })} 
      />
      <input 
        type="text" 
        placeholder="Tipo" 
        value={newVino.tipo} 
        onChange={(e) => setNewVino({ ...newVino, tipo: e.target.value })} 
      />
      <input 
        type="text" 
        placeholder="Bodega" 
        value={newVino.bodega} 
        onChange={(e) => setNewVino({ ...newVino, bodega: e.target.value })} 
      />
      <button onClick={handleCreateVino}>Crear Vino</button>

      {/* Buscar un vino por ID */}
      <h2>Buscar Vino por ID</h2>
      <input 
        type="text" 
        placeholder="ID del vino" 
        value={vinoId} 
        onChange={(e) => setVinoId(e.target.value)} 
      />
      <button onClick={handleGetVinoById}>Buscar Vino por ID</button>
      {vinoById && (
        <div>
          <h3>Detalle del Vino:</h3>
          <p>Nombre: {vinoById.nombre}</p>
          <p>Tipo: {vinoById.tipo}</p>
          <p>Bodega: {vinoById.bodega}</p>
        </div>
      )}

      {/* Buscar vinos por nombre */}
      <h2>Buscar Vinos por Nombre</h2>
      <input 
        type="text" 
        placeholder="Nombre del vino" 
        value={nombreBusqueda} 
        onChange={(e) => setNombreBusqueda(e.target.value)} 
      />
      <button onClick={handleGetVinoByNombre}>Buscar Vinos por Nombre</button>
      <ul>
        {vinosPorNombre.map(vino => (
          <li key={vino._id}>{vino.nombre} - {vino.tipo}</li>
        ))}
      </ul>
    </div>
  );
};

export default VinosCRUD;
