// src/pages/VinosUsers.js
import React, { useState, useEffect } from 'react';
import { getAllVinos } from '../services/vinoServices';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Image } from 'primereact/image';
import { Card } from 'primereact/card';
import { Rating } from 'primereact/rating';
import { Button } from 'primereact/button';
import './VinosUsers.css'; // Agrega un archivo CSS personalizado para estilos adicionales

const VinosUsers = () => {
  const [vinos, setVinos] = useState([]);

  useEffect(() => {
    fetchVinos();
  }, []);

  const fetchVinos = async () => {
    try {
      const data = await getAllVinos();
      setVinos(data);
    } catch (error) {
      console.error("Error al obtener los vinos:", error);
    }
  };

  const renderImagen = (rowData) => {
    return <Image src={rowData.imagen} alt={rowData.nombre} width="150" preview />;
  };

  const renderBodega = (rowData) => {
    return rowData.bodega ? rowData.bodega.nombre : 'Sin bodega asignada';
  };

  const renderAcciones = (rowData) => {
    return (
      <Button label="Ver Detalles" className="p-button-rounded p-button-info" />
    );
  };

  return (
    <div className="vinos-page p-mt-5 p-p-4" style={{ backgroundColor: '#f0f2f5', borderRadius: '10px' }}>
      <h1 className="p-text-center p-mb-4">Lista de Vinos</h1>
      <div className="p-grid p-justify-center">
        {vinos.length === 0 ? (
          <p>Cargando vinos...</p>
        ) : (
          <DataTable value={vinos} responsiveLayout="scroll" className="p-datatable-striped p-datatable-sm">
            <Column header="Imagen" body={renderImagen} />
            <Column field="nombre" header="Nombre del Vino" />
            <Column field="tipo" header="Tipo de Vino" />
            <Column header="Bodega" body={renderBodega} />
            <Column header="Acciones" body={renderAcciones} />
          </DataTable>
        )}
      </div>
    </div>
  );
};

export default VinosUsers;
