import React, { useState, useEffect } from "react";
import {
  crearVino,
  obtenerTodosLosVinos,
  obtenerVinoPorId,
  obtenerVinosPorNombre,
  actualizarVino,
  eliminarVino,
  obtenerVinosConPaginado,
} from "../services/vinoService";
import { InputText } from "primereact/inputtext";
import { Button } from "primereact/button";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Paginator } from "primereact/paginator";

const VinosPage = () => {
  // Estados para cada funcionalidad
  const [nuevoNombre, setNuevoNombre] = useState("");
  const [nuevoTipo, setNuevoTipo] = useState("");
  const [vinos, setVinos] = useState([]);
  const [vinoId, setVinoId] = useState("");
  const [vinoPorId, setVinoPorId] = useState(null);
  const [nombreFiltro, setNombreFiltro] = useState("");
  const [vinoNombre, setVinoNombre] = useState([]);
  const [idActualizar, setIdActualizar] = useState("");
  const [nombreActualizar, setNombreActualizar] = useState("");
  const [tipoActualizar, setTipoActualizar] = useState("");
  const [idEliminar, setIdEliminar] = useState("");
  const [totalRecords, setTotalRecords] = useState(0);
  const [page, setPage] = useState(1);
  const [rows, setRows] = useState(10);

  // Función para crear un vino
  const handleCrearVino = async () => {
    try {
      const nuevoVino = { nombre: nuevoNombre, tipo: nuevoTipo };
      const response = await crearVino(nuevoVino);
      alert(`Vino creado: ${response.nombre}`);
    } catch (error) {
      console.error("Error al crear el vino:", error);
    }
  };

  // Función para obtener todos los vinos sin filtros
  const handleObtenerTodosLosVinos = async () => {
    try {
      const data = await obtenerTodosLosVinos();
      setVinos(data);
    } catch (error) {
      console.error("Error al obtener todos los vinos:", error);
    }
  };

  // Función para obtener un vino por ID
  const handleObtenerVinoPorId = async () => {
    try {
      const data = await obtenerVinoPorId(vinoId);
      setVinoPorId(data);
    } catch (error) {
      console.error("Error al obtener el vino por ID:", error);
    }
  };

  // Función para obtener vinos por nombre
  const handleObtenerVinosPorNombre = async () => {
    try {
      const data = await obtenerVinosPorNombre(nombreFiltro);
      setVinoNombre(data);
    } catch (error) {
      console.error("Error al obtener vinos por nombre:", error);
    }
  };

  // Función para actualizar un vino
  const handleActualizarVino = async () => {
    try {
      const vinoActualizado = { nombre: nombreActualizar, tipo: tipoActualizar };
      const response = await actualizarVino(idActualizar, vinoActualizado);
      alert(`Vino actualizado: ${response.nombre}`);
    } catch (error) {
      console.error("Error al actualizar el vino:", error);
    }
  };

  // Función para eliminar un vino
  const handleEliminarVino = async () => {
    try {
      await eliminarVino(idEliminar);
      alert("Vino eliminado correctamente");
    } catch (error) {
      console.error("Error al eliminar el vino:", error);
    }
  };

  // Función para obtener vinos con paginación y ordenamiento
  const handleObtenerVinosConPaginado = async () => {
    const params = {
      page,
      limit: rows,
    };
    try {
      const data = await obtenerVinosConPaginado(params);
      setVinos(data.vinos);
      setTotalRecords(data.total);
    } catch (error) {
      console.error("Error al obtener vinos con paginación:", error);
    }
  };

  const onPageChange = (event) => {
    setPage(event.page + 1); // PrimeReact usa index 0 para la primera página
    setRows(event.rows);
  };

  return (
    <div className="p-grid p-justify-center">
      <div className="p-col-12 p-md-8">
        <h1>Gestión de Vinos</h1>

        {/* Crear vino */}
        <h3>Crear Vino</h3>
        <InputText
          placeholder="Nombre del vino"
          value={nuevoNombre}
          onChange={(e) => setNuevoNombre(e.target.value)}
        />
        <InputText
          placeholder="Tipo de vino"
          value={nuevoTipo}
          onChange={(e) => setNuevoTipo(e.target.value)}
        />
        <Button label="Crear" onClick={handleCrearVino} />

        {/* Obtener todos los vinos */}
        <h3>Obtener Todos los Vinos</h3>
        <Button label="Obtener Vinos" onClick={handleObtenerTodosLosVinos} />
        <DataTable value={vinos} responsiveLayout="scroll">
          <Column field="nombre" header="Nombre" />
          <Column field="tipo" header="Tipo" />
        </DataTable>

        {/* Obtener vino por ID */}
        <h3>Obtener Vino por ID</h3>
        <InputText
          placeholder="ID del vino"
          value={vinoId}
          onChange={(e) => setVinoId(e.target.value)}
        />
        <Button label="Obtener Vino" onClick={handleObtenerVinoPorId} />
        {vinoPorId && (
          <div>
            <p>Nombre: {vinoPorId.nombre}</p>
            <p>Tipo: {vinoPorId.tipo}</p>
          </div>
        )}

        {/* Obtener vinos por nombre */}
        <h3>Filtrar Vinos por Nombre</h3>
        <InputText
          placeholder="Nombre del vino"
          value={nombreFiltro}
          onChange={(e) => setNombreFiltro(e.target.value)}
        />
        <Button label="Buscar" onClick={handleObtenerVinosPorNombre} />
        <DataTable value={vinoNombre} responsiveLayout="scroll">
          <Column field="nombre" header="Nombre" />
          <Column field="tipo" header="Tipo" />
        </DataTable>

        {/* Actualizar vino */}
        <h3>Actualizar Vino</h3>
        <InputText
          placeholder="ID del vino a actualizar"
          value={idActualizar}
          onChange={(e) => setIdActualizar(e.target.value)}
        />
        <InputText
          placeholder="Nuevo nombre"
          value={nombreActualizar}
          onChange={(e) => setNombreActualizar(e.target.value)}
        />
        <InputText
          placeholder="Nuevo tipo"
          value={tipoActualizar}
          onChange={(e) => setTipoActualizar(e.target.value)}
        />
        <Button label="Actualizar" onClick={handleActualizarVino} />

        {/* Eliminar vino */}
        <h3>Eliminar Vino</h3>
        <InputText
          placeholder="ID del vino a eliminar"
          value={idEliminar}
          onChange={(e) => setIdEliminar(e.target.value)}
        />
        <Button label="Eliminar" onClick={handleEliminarVino} />

        {/* Obtener vinos con paginación */}
        <h3>Vinos con Paginación</h3>
        <Button label="Obtener Vinos con Paginado" onClick={handleObtenerVinosConPaginado} />
        <DataTable value={vinos} responsiveLayout="scroll">
          <Column field="nombre" header="Nombre" />
          <Column field="tipo" header="Tipo" />
        </DataTable>
        <Paginator
          first={(page - 1) * rows}
          rows={rows}
          totalRecords={totalRecords}
          onPageChange={onPageChange}
        />
      </div>
    </div>
  );
};

export default VinosPage;
