import axios from "axios";

// URL base para las APIs de vinos
const API_URL_VINOS = "http://localhost:3000/vinos";

// Obtener todos los vinos con filtros, ordenamiento y paginación
const obtenerVinos = async (params = {}) => {
  try {
    const response = await axios.get(API_URL_VINOS, { params });
    return response.data;
  } catch (error) {
    console.error("Error al obtener vinos:", error);
    throw error;
  }
};

// Obtener vinos con paginación explícita
const obtenerVinosConPaginado = async (params) => {
    try {
      const response = await axios.get(`http://localhost:3000/vinos/paginado`, { params });
      return response.data;
    } catch (error) {
      console.error("Error al obtener vinos con paginación:", error);
      throw error;
    }
  };

// Obtener un vino por ID
const obtenerVinoPorId = async (id) => {
  try {
    const response = await axios.get(`${API_URL_VINOS}/id/${id}`);
    return response.data;
  } catch (error) {
    console.error(`Error al obtener el vino con ID ${id}:`, error);
    throw error;
  }
};

// Obtener vinos por nombre
const obtenerVinosPorNombre = async (nombre) => {
  try {
    const response = await axios.get(`${API_URL_VINOS}/nombre/${nombre}`);
    return response.data;
  } catch (error) {
    console.error(`Error al obtener vinos por nombre ${nombre}:`, error);
    throw error;
  }
};

// Crear un nuevo vino
const crearVino = async (nuevoVino) => {
  try {
    const response = await axios.post(API_URL_VINOS, nuevoVino);
    return response.data;
  } catch (error) {
    console.error("Error al crear un nuevo vino:", error);
    throw error;
  }
};

// Actualizar un vino por ID
const actualizarVino = async (id, vinoActualizado) => {
  try {
    const response = await axios.put(`${API_URL_VINOS}/id/${id}`, vinoActualizado);
    return response.data;
  } catch (error) {
    console.error(`Error al actualizar el vino con ID ${id}:`, error);
    throw error;
  }
};

// Eliminar un vino por ID
const eliminarVino = async (id) => {
  try {
    const response = await axios.delete(`${API_URL_VINOS}/id/${id}`);
    return response.data;
  } catch (error) {
    console.error(`Error al eliminar el vino con ID ${id}:`, error);
    throw error;
  }
};

export {
  obtenerVinos,
  obtenerVinosConPaginado,
  obtenerVinoPorId,
  obtenerVinosPorNombre,
  crearVino,
  actualizarVino,
  eliminarVino,
};
