import React, { useEffect, useState } from "react";
import { obtenerBodegas, crearBodega, eliminarBodega } from "../services/bodegaService";
import BodegaForm from "../components/bodegaForm"; // Componente para el formulario

const BodegasPage = () => {
  const [bodegas, setBodegas] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [nuevaBodega, setNuevaBodega] = useState("");

  useEffect(() => {
    const fetchBodegas = async () => {
      try {
        const data = await obtenerBodegas();
        setBodegas(data);
      } catch (error) {
        console.error("Error al obtener las bodegas:", error);
        setError("Hubo un problema al cargar las bodegas.");
      } finally {
        setLoading(false);
      }
    };

    fetchBodegas();
  }, []);

  const handleCrearBodega = async (bodega) => {
    try {
      const nueva = await crearBodega(bodega);
      setBodegas([...bodegas, nueva]); // Añadir la nueva bodega a la lista
    } catch (error) {
      console.error("Error al crear la bodega:", error);
    }
  };

  const handleEliminarBodega = async (id) => {
    try {
      await eliminarBodega(id);
      setBodegas(bodegas.filter((bodega) => bodega._id !== id));
    } catch (error) {
      console.error(`Error al eliminar la bodega con ID ${id}:`, error);
    }
  };

  if (loading) {
    return <p>Cargando bodegas...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div>
      <h1>Lista de Bodegas</h1>

      {/* Formulario para agregar una nueva bodega */}
      <BodegaForm onSubmit={handleCrearBodega} />

      {/* Lista de bodegas */}
      <ul>
        {bodegas.map((bodega) => (
          <li key={bodega._id}>
            {bodega.nombre}
            <button onClick={() => handleEliminarBodega(bodega._id)}>
              Eliminar
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BodegasPage;
