import axios from "axios";

// URL base para las APIs de vinos
const API_URL_VINOS = "http://localhost:3000/vinos";

// Obtener todos los vinos (GET /vinos)
const obtenerTodosLosVinos = async () => {
  try {
    const response = await axios.get(API_URL_VINOS);
    return response.data;
  } catch (error) {
    console.error("Error al obtener los vinos:", error);
    throw error;
  }
};

// Obtener un vino por ID (GET /vinos/:id)
const obtenerVinoPorId = async (id) => {
  try {
    const response = await axios.get(`${API_URL_VINOS}/${id}`);
    return response.data;
  } catch (error) {
    console.error(`Error al obtener el vino con ID ${id}:`, error);
    throw error;
  }
};

// Agregar un nuevo vino (POST /vinos)
const agregarVino = async (nuevoVino) => {
  try {
    const response = await axios.post(API_URL_VINOS, nuevoVino);
    return response.data;
  } catch (error) {
    console.error("Error al agregar el vino:", error);
    throw error;
  }
};

// Actualizar un vino por ID (PUT /vinos/:id)
const actualizarVino = async (id, vinoActualizado) => {
  try {
    const response = await axios.put(`${API_URL_VINOS}/${id}`, vinoActualizado);
    return response.data;
  } catch (error) {
    console.error(`Error al actualizar el vino con ID ${id}:`, error);
    throw error;
  }
};

// Eliminar un vino por ID (DELETE /vinos/:id)
const eliminarVino = async (id) => {
  try {
    const response = await axios.delete(`${API_URL_VINOS}/${id}`);
    return response.data;
  } catch (error) {
    console.error(`Error al eliminar el vino con ID ${id}:`, error);
    throw error;
  }
};

export {
  obtenerTodosLosVinos,
  obtenerVinoPorId,
  agregarVino,
  actualizarVino,
  eliminarVino,
};
