import React, { useState, useEffect } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
import { Paginator } from 'primereact/paginator';
import { Dialog } from 'primereact/dialog';
import { InputText } from 'primereact/inputtext';
import {
  obtenerBodegasConPaginado,
  crearBodega,
  actualizarBodega,
  eliminarBodega,
  agregarVinoABodega,
  eliminarVinoDeBodega,
} from '../services/BodegaService';

const BodegasPage = () => {
    const [bodegas, setBodegas] = useState([]);
    const [page, setPage] = useState(1);
    const [totalRecords, setTotalRecords] = useState(0);
  
    useEffect(() => {
      fetchBodegas(page);
    }, [page]);
  
    const fetchBodegas = async (page) => {
      try {
        const response = await obtenerBodegasConPaginado(page, 10);
        setBodegas(response.data.bodegas);
        setTotalRecords(response.data.totalBodegas);
      } catch (error) {
        console.error("Error al obtener bodegas paginadas", error);
      }
    };
  
    const onPageChange = (event) => {
      setPage(event.page + 1);
    };
  
    return (
      <div className="p-mt-5 p-p-4" style={{ backgroundColor: '#f4f4f9', borderRadius: '10px' }}>
        <h1 className="p-text-center p-mb-4">Gesti√≥n de Bodegas</h1>
  
        <DataTable value={bodegas} paginator={false} responsiveLayout="scroll" className="p-datatable-striped p-datatable-sm">
          <Column field="nombre" header="Nombre" />
          <Column field="vinos" header="Vinos" body={(rowData) => rowData.vinos.length} />
          <Column header="Acciones" body={(rowData) => (
            <div className="p-d-flex p-ai-center">
              <Button icon="pi pi-pencil" className="p-button-rounded p-button-warning p-mr-2" />
              <Button icon="pi pi-trash" className="p-button-rounded p-button-danger" />
            </div>
          )} />
        </DataTable>
  
        <Paginator first={(page - 1) * 10} rows={10} totalRecords={totalRecords} onPageChange={onPageChange} className="p-mt-4" />
      </div>
    );
  };
  

export default BodegasPage;
